var google;function initGoogleMaps(){console.log('Initializing Google Maps...');var myLatlng=new google.maps.LatLng(29.6864011,-82.3899576);var mapOptions={zoom:6,center:myLatlng,scrollwheel:false,styles:[{"featureType":"administrative.land_parcel","elementType":"all","stylers":[{"visibility":"off"}]},{"featureType":"landscape.man_made","elementType":"all","stylers":[{"visibility":"off"}]},{"featureType":"poi","elementType":"labels","stylers":[{"visibility":"off"}]},{"featureType":"road","elementType":"labels","stylers":[{"visibility":"simplified"},{"lightness":20}]},{"featureType":"road.highway","elementType":"geometry","stylers":[{"hue":"#f49935"}]},{"featureType":"road.highway","elementType":"labels","stylers":[{"visibility":"simplified"}]},{"featureType":"road.arterial","elementType":"geometry","stylers":[{"hue":"#fad959"}]},{"featureType":"road.arterial","elementType":"labels","stylers":[{"visibility":"off"}]},{"featureType":"road.local","elementType":"geometry","stylers":[{"visibility":"simplified"}]},{"featureType":"road.local","elementType":"labels","stylers":[{"visibility":"simplified"}]},{"featureType":"transit","elementType":"all","stylers":[{"visibility":"off"}]},{"featureType":"water","elementType":"all","stylers":[{"hue":"#a1cdfc"},{"saturation":30},{"lightness":49}]}]};var mapElement=document.getElementById('map');var map=new google.maps.Map(mapElement,mapOptions);placeMarkersFromGeolocationArray(map);}
function placeMarkersFromGeolocationArray(map){var addresses=[{name:'Georgia, USA',location:{lat:32.165622,lng:-82.900075}},{name:'Florida, USA',location:{lat:27.664827,lng:-81.515754}},];for(var x=0;x<addresses.length;x++){var loc=addresses[x].location;var latlng=new google.maps.LatLng(loc.lat,loc.lng);new google.maps.Marker({position:latlng,map:map});}}